void movablemap::Render()
{
    SDL_RenderCopy(renderer, tex, &srcR, &destR);
}

void movablemap::battlecry(const char *texture,int segment)
{
     const int fps = 60;
        const int frame_delay = 1000 / fps;

        Uint32 frame_start;
        int frame_time;

        game = new Game();
        game->init(SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, 1280, 720, false, texture, segment);

        while (game->running())
        {
            frame_start = SDL_GetTicks();

            SDL_RenderClear(Game::renderer);

            game->handleEvents();
            game->update();
            game->render();

            frame_time = SDL_GetTicks() - frame_start;

            if (frame_delay > frame_time)
            {
                SDL_Delay(frame_delay - frame_time);
            }
        }

            game->clean();
    
        free(game);
}

int fight_trigger(){
    if(rand()%800 == 799){
        return 1;
    }
    return 0;
}
